package po;

public class Comment {
	private String mcomment;
	private String uname;
	private String date;
	private String blogId;
	public Comment() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Comment(String mcomment, String uname, String date, String blogId) {
		super();
		this.mcomment = mcomment;
		this.uname = uname;
		this.date = date;
		this.blogId = blogId;
	}
	
	public Comment(String mcomment, String uname, String date) {
		super();
		this.mcomment = mcomment;
		this.uname = uname;
		this.date = date;
	}
	public String getMcomment() {
		return mcomment;
	}
	public void setMcomment(String mcomment) {
		this.mcomment = mcomment;
	}
	public String getUname() {
		return uname;
	}
	public void setUname(String uname) {
		this.uname = uname;
	}
	public String getDate() {
		return date;
	}
	public void setDate(String date) {
		this.date = date;
	}
	public String getBlogId() {
		return blogId;
	}
	public void setBlogId(String blogId) {
		this.blogId = blogId;
	}
	@Override
	public String toString() {
		return "Comment [mcomment=" + mcomment + ", uname=" + uname + ", date=" + date + ", blogId=" + blogId + "]";
	}
	
	
	
	
}
